    private boolean esCadena(String entrada) {
    return entrada.matches(".*[a-zA-Z].*");
}

private boolean esNumero(String entrada) {
    try {
        Double.parseDouble(entrada);
        return true;
    } catch (NumberFormatException e) {
        return false;
    }
}

