    private void jButtonBuscarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButtonBuscarMouseClicked
        try {
      
        String buscar = jTextField3.getText();
        int contador = 0;

        if (esCadena(buscar)) {
           
            String cadena = jTextFieldCaracter.getText();
            for (int i = 0; i < cadena.length(); i++) {
                if (cadena.charAt(i) == buscar.charAt(0)) {
                    contador++;
                }
            }
        } else if (esNumero(buscar)) {
      jTextField4.setText("Valor a buscar inválido");
            return; 
           
        } 

    
         jTextField4.setText("La letra '" + buscar + "' aparece " + contador + " veces en la cadena.");
    } catch (Exception e) {
      
        jTextField4.setText("Por favor, ingrese una letra válida para buscar.");
    }
    String buscar = jTextField3.getText();
    int contador = 0;

    if (buscar.length() == 1) {
        
        String cadena = jTextFieldCaracter.getText();

        for (int i = 0; i < cadena.length(); i++) {
            if (cadena.charAt(i) == buscar.charAt(0)) {
                contador++;
            }
        }

       
       
    } else {
        
        jTextField4.setText("Por favor, ingrese una letra válida para buscar.");
    }
                       
    }//GEN-LAST:event_jButtonBuscarMouseClicked

    private void jButtonBuscar2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButtonBuscar2MouseClicked
        try {
        // Intenta obtener el valor a buscar desde el JTextField
        String buscar = jTextField5.getText();
        int contador = 0;

       if (esNumero(buscar))  {
            
            String cadena = jTextFieldCaracter.getText();
            for (int i = 0; i < cadena.length(); i++) {
                if (Character.isDigit(cadena.charAt(i)) && cadena.charAt(i) == buscar.charAt(0)) {
                    contador++;
                }
            }
        } else if (esCadena(buscar)) {
           jTextField6.setText("Valor a buscar inválido");
            return; 
        } 

        // Muestra el resultado en un JLabel o en cualquier otro lugar según tus necesidades.
       jTextField6.setText("El número " + buscar + " aparece " + contador + " veces en la cadena.");
    } catch (Exception e) {
        // Manejar cualquier excepción que pueda ocurrir durante la búsqueda.
           jTextField6.setText("Por favor, ingrese un número válido para buscar.");
    }

        

    
    
    }//GEN-LAST:event_jButtonBuscar2MouseClicked

